import com.machinedoll.wonderland.CountAndSay
import org.scalatest.funsuite.AnyFunSuite

class CountAndSayTest extends AnyFunSuite {
  val checker = new CountAndSay()
  test("1 is 1") {
    assert(checker.countAndSay(1) === "1")
  }

  test("31 is \'\'") {
    assert(checker.countAndSay(31) === "")
  }

  test("-1 is \"\"") {
    assert(checker.countAndSay(-1) === "")
  }

  test("2 is 11") {
    assert(checker.countAndSay(2) === "11")
  }

  test("3 is 21") {
    assert(checker.countAndSay(3) === "21")
  }

  test("4 is 1211") {
    assert(checker.countAndSay(4) === "1211")
  }

  test("5 is 111221") {
    assert(checker.countAndSay(5) === "111221")
  }

